import { useEffect, useState } from 'react';

const MyComponent = () => {
    const [products, setProducts] = useState<any[]>([]);

    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then(res => res.json())
            .then(json => setProducts(json));  // Armazenando os produtos no estado
    }, []);  // Efeito que ocorre apenas uma vez, quando o componente for montado

    return (
        <section className="main-items">
            {products.map((item) => (
                <div className="item" key={item.id}>
                    <img src={item.image || "https://cdn2.thecatapi.com/images/2ch.jpg"} alt="Produto" />
                    <label>Name: {item.title}</label>
                    <label>Price: ${item.price}</label>
                    <button>Comprar Agora!</button>
                </div>
            ))}
        </section>
    );
};

export default MyComponent;
